DROP TABLE IF EXISTS `receiver`;CREATE TABLE `receiver` (  `id` int(8) NOT NULL AUTO_INCREMENT,  `version` decimal(10,0) DEFAULT NULL,  `createtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  `lastupdatetime` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00',  `username` varchar(255) DEFAULT NULL,  `receiverMan` varchar(255) DEFAULT NULL,  `receiverManPhone` varchar(255) DEFAULT NULL,  `receiverManAddress` varchar(255) DEFAULT NULL,  `receiverPostcode` varchar(255) DEFAULT NULL,  `receiverCity` varchar(255) DEFAULT NULL,  `receiverProvince` varchar(255) DEFAULT NULL,  `receiverCounty` varchar(255) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;DROP TABLE IF EXISTS `sender`;CREATE TABLE `sender` (  `id` int(8) NOT NULL AUTO_INCREMENT,  `version` decimal(10,0) DEFAULT NULL,  `createtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  `lastupdatetime` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00',  `username` varchar(255) DEFAULT NULL,  `senderMan` varchar(255) DEFAULT NULL,  `senderManPhone` varchar(255) DEFAULT NULL,  `senderManAddress` varchar(255) DEFAULT NULL,  `senderPostcode` varchar(255) DEFAULT NULL,  `senderCity` varchar(255) DEFAULT NULL,  `senderProvince` varchar(255) DEFAULT NULL,  `senderCounty` varchar(255) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;DROP TABLE IF EXISTS `shippingrequest`;CREATE TABLE `shippingrequest` (  `id` int(19) NOT NULL AUTO_INCREMENT,  `version` decimal(10,0) DEFAULT NULL,  `createtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  `lastupdatetime` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00',  `orderSource` varchar(255) DEFAULT NULL,  `orderId` varchar(255) DEFAULT NULL,  `batchId` varchar(255) DEFAULT NULL,  `userId` decimal(19,0) DEFAULT NULL,  `machineCode` varchar(255) DEFAULT NULL,  `needFetch` decimal(1,0) DEFAULT NULL,  `isReprint` decimal(1,0) DEFAULT NULL,  `receiverMan` varchar(255) DEFAULT NULL,  `receiverManPhone` varchar(255) DEFAULT NULL,  `logisticsProviderCode` varchar(255) DEFAULT NULL,  `receiverProvince` varchar(255) DEFAULT NULL,  `receiverCity` varchar(255) DEFAULT NULL,  `receiverCounty` varchar(255) DEFAULT NULL,  `receiverManAddress` varchar(255) DEFAULT NULL,  `receiverPostcode` varchar(255) DEFAULT NULL,  `senderMan` varchar(255) DEFAULT NULL,  `senderManPhone` varchar(255) DEFAULT NULL,  `senderProvince` varchar(255) DEFAULT NULL,  `senderCity` varchar(255) DEFAULT NULL,  `senderCounty` varchar(255) DEFAULT NULL,  `senderManAddress` varchar(255) DEFAULT NULL,  `senderPostcode` varchar(255) DEFAULT NULL,  `itemName` varchar(255) DEFAULT NULL,  `itemCount` varchar(255) DEFAULT NULL,  `itemWeight` varchar(255) DEFAULT NULL,  `remark` varchar(255) DEFAULT NULL,  `remark1` varchar(255) DEFAULT NULL,  `remark2` varchar(255) DEFAULT NULL,  `remark3` varchar(255) DEFAULT NULL,  `udf1` varchar(255) DEFAULT NULL,  `udf2` varchar(255) DEFAULT NULL,  `udf3` varchar(255) DEFAULT NULL,  `udf4` varchar(255) DEFAULT NULL,  `udf5` varchar(255) DEFAULT NULL,  `udf6` varchar(255) DEFAULT NULL,  `udf7` varchar(255) DEFAULT NULL,  `udf8` varchar(255) DEFAULT NULL,  `errorCode` varchar(255) DEFAULT NULL,  `errorDesc` varchar(255) DEFAULT NULL,  `eShippingOrderString` text,  `result` varchar(255) DEFAULT NULL,  `printResult` varchar(255) DEFAULT NULL,  `mailNo` varchar(255) DEFAULT NULL,  `mainId` decimal(19,0) DEFAULT NULL,  `shippingStatus` varchar(255) DEFAULT NULL,  `username` varchar(255) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;DROP TABLE IF EXISTS `users`;CREATE TABLE `users` (  `userId` decimal(19,0) DEFAULT NULL,  `username` varchar(255) NOT NULL,  `password` varchar(255) NOT NULL,  PRIMARY KEY (`username`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;